E_D("DROP TABLE IF EXISTS `imicms_img`;");
E_C("CREATE TABLE `imicms_img` (
  `id` int(11) NOT NULL auto_increment,
  `uid` int(11) NOT NULL,
  `uname` varchar(90) NOT NULL,
  `keyword` char(255) NOT NULL,
  `type` varchar(1) NOT NULL COMMENT '关键词匹配类型',
  `text` text NOT NULL COMMENT '简介',
  `classid` int(11) NOT NULL,
  `classname` varchar(60) NOT NULL,
  `pic` char(255) NOT NULL COMMENT '封面图片',
  `showpic` varchar(1) NOT NULL COMMENT '图片是否显示封面',
  `info` text NOT NULL COMMENT '图文详细内容',
  `url` char(255) NOT NULL COMMENT '图文外链地址',
  `createtime` varchar(13) NOT NULL,
  `uptatetime` varchar(13) NOT NULL,
  `click` int(11) NOT NULL,
  `token` char(30) NOT NULL,
  `title` varchar(60) NOT NULL,
  `sorts` varchar(6) NOT NULL,
  `usort` int(11) NOT NULL default '1',
  `precisions` tinyint(1) NOT NULL default '0',
  `longitude` varchar(20) NOT NULL default '0',
  `latitude` varchar(20) NOT NULL default '0',
  `writer` varchar(200) default NULL COMMENT '作者',
  `texttype` int(11) NOT NULL default '1' COMMENT '文本类型',
  `usorts` int(11) NOT NULL default '1' COMMENT '分类文章排列顺序',
  `is_focus` tinyint(4) NOT NULL,
  `pinglun` int(1) unsigned NOT NULL default '1' COMMENT '是否开启当前图文评论',
  `wechat_group` varchar(500) NOT NULL,
  `media_id` varchar(200) NOT NULL default '',
  PRIMARY KEY  (`id`),
  KEY `classid` USING BTREE (`classid`),
  KEY `uid` USING BTREE (`uid`)
) ENGINE=MyISAM AUTO_INCREMENT=301 DEFAULT CHARSET=utf8");
E_D("replace into `imicms_img` values('1','1','eake','产品','2','社会发展到今天，网络已成为人们生活的重要组成部分，通过网络武装宣传自己，已成为现代企业发展的必由之路。','1','产品','http://wx.eake.cn/tpl/Static/kindeditors/attached/99630ff411650cfa/image/20140923/99630ff411650cfa2014092313534847012.jpg','1','&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	&lt;h2 class=&quot;title js-ask-title&quot; style=&quot;font-size:16px;color:#2E2E2E;font-family:&#039;Microsoft Yahei&#039;;background-color:#FFFFFF;&quot;&gt;\r\n		企业为什么要建设网站？\r\n	&lt;/h2&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;color:#333333;font-family:&#039;Microsoft Yahei&#039;;font-size:14px;line-height:28px;background-color:#FFFFFF;&quot;&gt;简单地说，企业建立网站的目的主要有以下方面:&amp;nbsp;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;color:#333333;font-family:&#039;Microsoft Yahei&#039;;font-size:14px;line-height:28px;background-color:#FFFFFF;&quot;&gt;1、希望得到更多的人以更快捷的方式了解您的企业和产品，提高企业在同行业中的知名度从而增强企业的竞争力;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;color:#333333;font-family:&#039;Microsoft Yahei&#039;;font-size:14px;line-height:28px;background-color:#FFFFFF;&quot;&gt;2、希望获得更多客户订单;&amp;nbsp;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;color:#333333;font-family:&#039;Microsoft Yahei&#039;;font-size:14px;line-height:28px;background-color:#FFFFFF;&quot;&gt;3、希望更有效地控制企业宣传成本;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;color:#333333;font-family:&#039;Microsoft Yahei&#039;;font-size:14px;line-height:28px;background-color:#FFFFFF;&quot;&gt;4、希望通过网站的形式无形延长企业营业时间，客户可以在任何时间浏览&lt;/span&gt;企业网站&lt;span style=&quot;color:#333333;font-family:&#039;Microsoft Yahei&#039;;font-size:14px;line-height:28px;background-color:#FFFFFF;&quot;&gt;，选择自己所需的产品而无须任何营业人员在线服务;&amp;nbsp;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:&#039;Microsoft Yahei&#039;;font-size:14px;line-height:28px;background-color:#FFFFFF;&quot;&gt;5、希望开拓本地市场，发展港奥台及国际业务 \r\n 对于小企业通过传统方式要发展国际业务是有一定难度的，但通过在internet上建立自己的企业网站，发布您的企业形象及从事的业务，将被世界上每一个拥有电脑和对您的业务感兴趣的人所看到，即便是小公司，也可轻易与大公司竞争，在Internet上&lt;/span&gt;&lt;span style=&quot;line-height:1.5;&quot;&gt;人人&lt;/span&gt;&lt;span style=&quot;font-family:&#039;Microsoft Yahei&#039;;font-size:14px;line-height:28px;background-color:#FFFFFF;&quot;&gt;平等。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;color:#333333;font-family:&#039;Microsoft Yahei&#039;;font-size:14px;line-height:28px;background-color:#FFFFFF;&quot;&gt;6、希望与自己的客户保持密切及时联系\r\n 在internet上，网站24小时都在运营中，顾客在任何时候，任何地点都可与企业保持及时密切的联系，这是传统业务联系无法比拟的。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	&lt;h3&gt;\r\n		官方网站：&lt;a href=&quot;http://qy.eake.cn&quot; target=&quot;_blank&quot;&gt;qy.eake.cn&lt;/a&gt;&amp;nbsp;建议通过电脑访问获得最佳效果\r\n	&lt;/h3&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;color:#7F7F7F;&quot;&gt;&lt;span style=&quot;color:#FF0000;&quot;&gt;&lt;span style=&quot;background-color:#000000;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;color:#64451D;&quot;&gt;如果您喜欢我们的内容请动动小手指转发到您的朋友圈&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;b&gt;您还可以点击：&lt;a href=&quot;http://mp.weixin.qq.com/s?__biz=MzA3MTE4OTgyNg==&amp;amp;mid=201017818&amp;amp;idx=1&amp;amp;sn=d4fcf7d2a518d4b3fc1d4c4f971ffc21#rd&quot;&gt;亚篮网公众服务&lt;/a&gt;&amp;nbsp;&amp;nbsp;关注我们&lt;/b&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;','','1411451661','1415677816','54','99630ff411650cfa','企业网站建设','1','1','0','0','0',NULL,'1','1','0','1','','');");
E_D("replace into `imicms_img` values('204','6','1457963911','知识','1','全球最值得旅游的十大典范之地','11','生活小常识','http://wx.eake.cn/uploads/2/257e21c8a9e1be8c/d/e/8/1/thumb_5462ca6fb5211.jpg','1','&lt;p&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;line-height:2em;color:#0070C0;&quot;&gt;&lt;span style=&quot;line-height:2em;background-color:#C3D69B;color:#3E3E3E;&quot;&gt;&lt;strong&gt;&lt;span&gt;【更多你喜欢的】&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;strong&gt;&lt;span&gt;点击标题上面蓝色字“&lt;span style=&quot;color:#0070C0;&quot;&gt;&lt;a href=&quot;http://mp.weixin.qq.com/s?__biz=MjM5NjYyNDU3MA==&amp;mid=201415213&amp;idx=1&amp;sn=b22d138667085247c432f403f46815c5#rd&quot; target=&quot;_blank&quot;&gt;杰诚生活&lt;/a&gt;&lt;/span&gt;”关注&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;div&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;line-height:2em;color:#0070C0;&quot;&gt;&lt;strong&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/div&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;img src=&quot;http://mmbiz.qpic.cn/mmbiz/BFobaMTWicLJBfrQc9bYXJhzib4zdTmIRsMwK6YtDqGmM4X3JtkCoZs8oH5xtGbx6IPFqFHENdMLqIPME8mtwuDQ/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;h2 class=&quot;rich_media_title&quot; id=&quot;activity-name&quot; style=&quot;font-size:20px;&quot;&gt;\r\n		全球最值得旅游的十大典范之地\r\n	&lt;/h2&gt;\r\n	&lt;div class=&quot;rich_media_meta_list&quot;&gt;\r\n		2014-11-12&amp;nbsp;杰诚&amp;nbsp;&lt;a class=&quot;rich_media_meta link nickname&quot; id=&quot;post-user&quot;&gt;杰诚生活&lt;/a&gt;\r\n	&lt;/div&gt;\r\n	&lt;div id=&quot;page-content&quot;&gt;\r\n		&lt;div id=&quot;img-content&quot;&gt;\r\n			&lt;div class=&quot;rich_media_content&quot; id=&quot;js_content&quot; style=&quot;color:#3E3E3E;font-size:16px;&quot;&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img src=&quot;http://mmbiz.qpic.cn/mmbiz/BFobaMTWicLJBfrQc9bYXJhzib4zdTmIRsMwK6YtDqGmM4X3JtkCoZs8oH5xtGbx6IPFqFHENdMLqIPME8mtwuDQ/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					生态旅游是时下相当热门红火的概念，它是指惠及当地经济发展的同时，最大限度的减小对当地社会环境和生态环境的破坏，帮助当地居民保护他们的居住地及其赖以生存的社会文化。生态旅游概念一经提出，便受到了大众的欢迎和赞同。那么，如何判断旅游目的地是否是生态旅游的理想之地呢?哪些地方称得上是生态旅游的典范呢?让我们跟随美国雅虎网站10月22日的报道一起去解答这些问题吧!\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;1. 佛得角(Cape Verde)&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img id=&quot;c80&quot; width=&quot;auto&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/fP0ymJ1UvVe2TFu8iaAEKT3dgrD8YWyoAQ812hAVpWrU9henbxDGf9wrM8JRdyCFA0EDCuVlzEA80shnDytkX5g/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					佛得角，葡萄牙群岛上的一颗明珠，素被誉为“政治权利和公民权利的典范”。就在最近，它举办了非洲史上第二次同性恋游行。去年，世界银行对佛得角在扩大旅游产业同时为生态环保作出的努力表示赞赏。这个国家最近还宣布了它雄心勃勃的能源节约方案，提出了要在接下来的20年内能源利用率要达到百分之三十五。\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;2. 瓜伊哈纳斯(Gwaii Haanas)&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img id=&quot;c81&quot; width=&quot;auto&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/fP0ymJ1UvVe2TFu8iaAEKT3dgrD8YWyoAu6LfoiaGxoSJ6mjw7NRF1BCzrpeOpSsjyFqzZ3ibYzQ3EsXKMibvHzLpA/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					瓜伊哈纳斯位于加拿大西部不列颠哥伦比亚省，这颗加拿大明珠是土著居民和联邦政府相互协作的典范。哈纳斯国家公园和海达文化遗产地位于海达瓜伊最南端，这里地处偏僻，气候温润，是您生态旅游的理想之地!\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;3. 冰岛&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img id=&quot;c82&quot; width=&quot;auto&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/fP0ymJ1UvVe2TFu8iaAEKT3dgrD8YWyoArK0oSEt5o5MSw9ibvymdcyyXVaN2LIAI1eCx5wicJcGZbbwicwuibKMbdg/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					冰岛作为黄金旅游风景区可以说是长盛不衰，这其中一个很大的原因归功于其对资源重复利用的热衷。据称，冰岛80%的能源是通过再循环得到的，它一直致力于创造属于它自己的可持续发展文化。为了实现冰岛独有的生态旅游战略，它采用了各种激励措施，包括福利保障策略和北欧策略等。\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;4. 喀拉拉邦(Kerala)&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img id=&quot;c83&quot; width=&quot;auto&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/fP0ymJ1UvVe2TFu8iaAEKT3dgrD8YWyoAjz8gkpS0eTNr54NaolFr4vm7BSHoPqicicRNqrguTicabJc1oic6uB0CXg/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					有很多人认为印度不能称得上是生态国家，但是位于印度西南部的喀拉拉邦却是个例外。喀拉拉邦一直是生态旅游的先行者，当地居民把自家的米艇改造成居住船。依临瀑布，以树作屋，喀拉拉邦人民的生活完全与自然和谐融合起来。该邦在山地老虎自然保护区的建设上以及其神秘有效的印度式草药疗法在世界范围内都是无可比拟的。\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;5. 立陶宛&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img id=&quot;c84&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/fP0ymJ1UvVe2TFu8iaAEKT3dgrD8YWyoAYiao0MjdJo83aVUYmLx7YtpfKt6FFR4QajYRXNk6LokoaQxpSGeDGAw/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					立陶宛素以其在生态保护方面取得的卓越成就而为人所知。目前，立陶宛5岁以下儿童死亡率较2000年下降了52%，已经达到了其千年发展目标。立陶宛有严格的法律来保护妇女权利，当地妇女可拥有时长两年的带薪产假，堪称欧洲最长产假。\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;6. 蒙特沃德(Montverde)&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img id=&quot;c85&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/fP0ymJ1UvVe2TFu8iaAEKT3dgrD8YWyoA2dJb087Jv6ibluL1hibJpDaLRTOl8pDGgjplibs58PTSCRDuJKPqMukbg/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					蒙特沃德位于哥斯达黎加，以其云雾林中的生物多样性而闻名于世。这个山地环绕的城市安宁而富有自然美，当地居民热情好客。企业、旅客以及非政府组织共同合作为保护蒙特沃德的生态旅游募集善款。想体验正宗的生态旅游吗?这里就是您理想的选择。\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;7. 纳米比亚&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img id=&quot;c86&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/fP0ymJ1UvVe2TFu8iaAEKT3dgrD8YWyoAiaOQbzjVUIQCcVgscFJMoItJplPDTRqYRQOeg0QSV9ibJlhwwSdcsLng/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					尽管许多非洲国家自豪于为游客提供游猎机会，纳米比亚却将野生动物保护列入了它的宪法当中。纳米比亚与许多世界组织如世界野生动物基金合作，创建了许多野生动物保护区。其结果是不仅纳米比亚的野生动物受益于此，当地的居民的福利保障也有了相当大的提高。\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;8. 东北王国&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img id=&quot;c87&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/fP0ymJ1UvVe2TFu8iaAEKT3dgrD8YWyoARkic6VDt21Vm40EbrZ5ibwhTunOAf9Il903GYm9ZWZicicGz8EHFfCAjuw/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					东北王国位于佛蒙特州东北角，曾被世界地理杂志称为世界上最值得去的地方，其很大程度上这是因为东北王国极为切合生态旅游理念，为参观者们提供了本地化旅游体验机会。东北王国极力避免大众旅游，保护当地遗产文化，建立了以社区、小型企业为基础，以丰厚资源为依托的旅游产业。\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;9. 塞巴&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img id=&quot;c88&quot; width=&quot;auto&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/fP0ymJ1UvVe2TFu8iaAEKT3dgrD8YWyoA2EcnNgWfFK0tyqHqvD9Pl3prJ17EaibA3icI7eu4nLaVhwt55dWF5r8Q/0&quot; style=&quot;height:auto !important;width:698px !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					塞巴在拉丁群岛中不甚出名，然而正是因为有塞巴的存在，拉丁群岛的生态旅游才得以拥有如此大的吸引力。没有沙滩，没有海港，塞巴岛在加勒比海中肃然而立，因此也得以免除大众旅游对其生态环境的破坏。同时，塞巴致力于其周围海洋环境及山地森林的保护。\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;10. 萨摩亚(Samoa)&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img id=&quot;c89&quot; width=&quot;auto&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/fP0ymJ1UvVe2TFu8iaAEKT3dgrD8YWyoAtCcTfugFARYJiaJvSFjgDtGD1YcZicfmjia3N5UhosAYxxnx9yRPDg9aA/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					这个位于南太平洋的波利尼亚天堂，占地多达三千多平方公里，将自然风光和本土文化完美的结合起来。萨摩人非常珍视他们的海岸线、珊瑚礁、海洋生物以及雨林覆盖的火山，他们一直保持着集体的生活方式。\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;img width=&quot;331px&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/6xsuhALdNEr0ibLDATPbiaQoI0OyJzZP81jUpsBzUCDBCf1wr2YyIhQoqRTQ8D5ArKPDE0ZFSdicuP8ug1LJcbokA/0&quot; style=&quot;height:auto !important;width:331px !important;&quot; /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;background-color:#F8F7F5;&quot;&gt;&lt;img src=&quot;http://mmbiz.qpic.cn/mmbiz/L4Nl3dMp8S6FNtQVasqycvZ3zqjkjbeoD4omqNXeqXwF08FREWWeWjsQ2niagRrdeN6Th5lHQMpBSRiaPlibOC0PQ/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;background-color:#D99694;color:#FFFFFF;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;color:#444444;font-family:Tahoma, Helvetica, Arial, 微软雅黑, 华文细黑, 宋体, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;关注我们提供更多省钱攻略，有惊喜特价，温馨提示，各种当地服务！&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;color:#444444;font-family:Tahoma, Helvetica, Arial, 微软雅黑, 华文细黑, 宋体, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;如何尽快找到我们呢？亲！&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;安宁杰诚家政服务&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;联系电话：0871-68681294&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;移动电话：13888384509 15198862744&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;div&gt;\r\n					&lt;strong&gt;&lt;br /&gt;\r\n&lt;/strong&gt;\r\n				&lt;/div&gt;\r\n			&lt;/div&gt;\r\n		&lt;/div&gt;\r\n	&lt;/div&gt;\r\n&lt;/p&gt;','','1415760547','1415760547','0','257e21c8a9e1be8c','全球最值得旅游的十大典范之地','','203','0','0','0',NULL,'1','1','0','1','','');");
E_D("replace into `imicms_img` values('2','1','eake','产品','2','亚蓝微营销移动互联网营销引领者-助您企业拥有更好发展，走在互联网营销第一梯队中……','1','产品','http://wx.eake.cn/tpl/Static/kindeditors/attached/99630ff411650cfa/image/20140923/99630ff411650cfa2014092314054635408.jpg','1','&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;center&quot; style=&quot;text-align:center;&quot;&gt;\r\n	微信营销大时代，为您的企业带来无限商机&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	2013年&lt;span&gt;12&lt;/span&gt;月微信用户突破了&lt;span&gt;7&lt;/span&gt;亿，面对这庞大的用户群体，微营销孕育而生。什么是微营销？&lt;span class=&quot;MsoHyperlink&quot;&gt;&lt;a href=&quot;http://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=41523569&quot;&gt;&lt;span&gt;微营销&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;是现代一种低成本、高性价比的营销手段。与传统营销方式相比，“&lt;span class=&quot;MsoHyperlink&quot;&gt;&lt;a href=&quot;http://baike.sogou.com/lemma/ShowInnerLink.htm?lemmaId=41523569&quot;&gt;&lt;span&gt;微营销&lt;/span&gt;&lt;/a&gt;&lt;/span&gt;”主张通过“虚拟”与“现实”的互动，建立一个涉及研发、产品、渠道、市场、品牌传播、促销、客户关系等更“轻”、更高效的营销全链条，整合各类营销资源，达到了以小博大、以轻博重的营销效果。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	微信营销的优势：&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:0cm;background-color:white;vertical-align:baseline;&quot;&gt;\r\n	·&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n企业的商城开到每个人的手机里。对消费者来说，可以随时随地购物。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:0cm;background-color:white;vertical-align:baseline;&quot;&gt;\r\n	·&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n商品管理，商城后台具备商品上传、分类管理、订单处理等与网上店铺都具有的设置功能。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:0cm;background-color:white;vertical-align:baseline;&quot;&gt;\r\n	·&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n支付功能，支持支付宝，微信会员卡余额支付。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:0cm;background-color:white;vertical-align:baseline;&quot;&gt;\r\n	·&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n支持多个团购产品订单、团购限时限量、设置整合微支付、产品多个细节图、设定起始购买人数。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-left:0cm;text-indent:21.0pt;background:white;vertical-align:baseline;&quot;&gt;\r\n	微信会员卡其实相当于手机会员卡使用，商家通过微信公众平台账号后台设置二维码内容，让用户扫描二维码成为会员，手机会员卡通过微信自定义接口，实现微信会员卡和店面原有会员软件对接，使其具备了手机刷卡和手机互动功能，会员消费打折还有消费记录可以查询。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	对于会员来说，会员无需携带会员卡，会员卡保存在手机中；刷卡识别，刷手机识别会员身份；自助查询积分和消费记录，自助兑换礼品；反馈意见建议，预约场地或服务，查看最新优惠信息。\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	亚蓝微信营销平台优势：&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	1 操作简捷\r\n微易平台的管理界面简单易用操作方便，大量人性化设计&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	2 功能强大\r\n通过微易平台用户可以轻松管理自己的微信各类信息&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	3 专业团队\r\n一站式管理 平台支持 拥有多年网络营销经验的管理团队 强大的技术支持 专注于社会化媒体的研究&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	4 资深背景\r\n微易是由从事多年网站建设的爱迪森网络投资创建的微信公众服务平台，为商家和企业提供一个更智能更便捷的微信公共智能服务平台&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	5 价格优势\r\n微易在同行业中同等品质价格最低真正让用户获得最大利益&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	6 微网站多元化\r\n亚蓝微信营销平台已经开发出&lt;span&gt;100&lt;/span&gt;多套微网站，覆盖全行业，是全网微网站最多的平台，同时支持自定义微网站设计。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	亚蓝微信营销平台的功能：&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;b&gt;微网站：&lt;/b&gt;是建立在微信平台的一个小型网站，只需要设置分类，添加图文，选择喜欢的模版就能生成一个漂亮的微信网站。可以展示企业、商家的各种信息，不用备案，方便，易浏览。微官网开创了&lt;span&gt;&quot;&lt;/span&gt;所见即所得&lt;span&gt;&quot;&lt;/span&gt;的精简版面设计，拥有属于自己的终身网站，不用注册域名，不用购买空间，不用复杂的操作，更不用进行网站备案，轻松的拥用自己可以随时制作与修改的网站。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;b&gt;微会员：&lt;/b&gt;通过亚蓝微信营销平台，商家可以为关注用户发放虚拟会员卡和优惠劵，商家不用再制作传统会员卡，省时省力省钱。将电子会员卡呈现在微信中，让更多的人通过网络、报刊、杂志等途径，通过扫描二维码方式，成为商家的会员，享受会员待遇。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;b&gt;微活动：&lt;/b&gt;在我们的亚蓝微信营销平台上，商家可创建幸运大转盘、优惠券、刮刮卡、砸金蛋等活动，设置活动内容、奖项及中奖比例，吸引客户关注，可以实现线上线下互动，吸引更多潜在客户。让企业获得了低成本组织的力量。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;b&gt;微订单：&lt;/b&gt;可以设置自定义在线订单，在线预约，在线报名等，可以应用到预约看房，试乘试驾，客房预订，酒店预订，预约挂号等多方面。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;background:white;&quot;&gt;\r\n	&lt;b&gt;微相册：&lt;/b&gt;设置相册封面图及上传图片，就可以得到一个精美的相册网站。非常适合婚纱行业、汽车行业或者需要展示产品图片的商家。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;b&gt;微商城：&lt;/b&gt;商家可在自己微信营销平台发布产品，设置价格及说明，即可实现在线商城的功能。同时可发布团购产品及酒店菜品等，能快速提升销售业绩。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;b&gt;微客服：&lt;/b&gt;实现在线&lt;span&gt;PC&lt;/span&gt;与微信端用户的实时交流。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;b&gt;微统计：&lt;/b&gt;帮助我们低成本的进行舆论监控和市场调查，实现了对市场的监控与及时应对。\r\n&lt;/p&gt;\r\n&lt;p class=&quot;MsoNormal&quot; align=&quot;left&quot; style=&quot;text-indent:21pt;&quot;&gt;\r\n	除以上介绍的功能，亚蓝微信营销平台还开发了很多功能，比如天气查询，笑话，公交车查询，健康查询，快递查询，翻译，彩票查询等小功能，让用户感觉方便，贴心，大大提高用户体验。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-left:0cm;background:white;vertical-align:baseline;&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;h3 style=&quot;margin-left:0cm;text-indent:21.0pt;background:white;vertical-align:baseline;&quot;&gt;\r\n	微信商城：&lt;span&gt;&lt;/span&gt; \r\n&lt;/h3&gt;\r\n&lt;p style=&quot;margin-left:0cm;text-indent:26.25pt;background:white;vertical-align:baseline;&quot;&gt;\r\n	微信商城是基于微信而研发的一款社会化轻量级电子商务系统，同时又是一款传统互联网、移动互联网、微信三网一体化的企业购物系统。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-left:0cm;text-indent:21.0pt;background:white;vertical-align:baseline;&quot;&gt;\r\n	它可以帮助企业发布商品到微信，在微信中建立自己的商城。利用微信的天然社交群，最大化的使商品在不同的人际网络中流传，加速企业在微信上建立自己的品牌专区，轻松打造微信企业形象，最大化拓展企业的品牌及销售，并且不会有第三方结算拖款。&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-left:0cm;background:white;vertical-align:baseline;&quot;&gt;\r\n	&amp;nbsp;\r\n&lt;/p&gt;\r\n微信营销在功能强大的亚蓝微信营销平台支持下，配合各种营销方案来运作，可以来达到一个超乎想象的微营销效果，我们针对不同行业有着不同的\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	&lt;span style=&quot;color:#1FA2E1;&quot;&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	不管您是商家或者是个人只要申请了公众号（不管订阅号、服务号、高级企业号）亚蓝微营销都能为您实现以下注释中的功能。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	注释：亚蓝信息技术有限公目前推出最具权威性微信第三方营销营销推广平台&lt;a href=&quot;http://wx.eake.cn&quot;&gt;wx.eake.cn&lt;/a&gt;，可在微信中实现功能有:短信群发、自定义菜单、微WIFI、订单提醒、微官网、360全景、喜帖、贺卡、微论坛、留言、摇一摇、微活动、行业有：微电商、投票、调研、房产、汽车、医疗、教育、酒店、CRM管理等。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	更多功能请查看以下联系方式\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	QQ：61091793\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	Emaill：eaekcn@qq.com\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	联系电话：18987133915\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	更多请电脑登陆：&lt;a href=&quot;http://wx.eake.cn/&quot; target=&quot;_blank&quot;&gt;http://wx.eake.cn/&lt;/a&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	&lt;span style=&quot;color:#585858;font-family:微软雅黑, Verdana, 宋体, Arial, Sans;line-height:normal;background-color:#EAEAEA;&quot;&gt;因为专业-所以选择！我们让互联网变得如此简单，诚信、共赢、共同进步！亚蓝微营销移动互联网营销引领者-助您企业拥有更好发展，走在互联网营销第一梯队中……&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#333333;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:13px;&quot;&gt;\r\n	&lt;span style=&quot;color:#585858;font-family:微软雅黑, Verdana, 宋体, Arial, Sans;line-height:normal;background-color:#EAEAEA;&quot;&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial