ong&gt;&lt;/span&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;text-align:center;color:#222222;font-family:Arial, Verdana, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;text-align:center;color:#222222;font-family:Arial, Verdana, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;span style=&quot;font-size:18px;color:#E36C09;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;line-height:2em;&quot;&gt;你若&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;text-align:center;color:#222222;font-family:Arial, Verdana, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;span style=&quot;font-size:18px;color:#E36C09;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;color:#4F81BD;line-height:2em;&quot;&gt;在我身旁，&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;text-align:center;color:#222222;font-family:Arial, Verdana, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;span style=&quot;font-size:18px;color:#E36C09;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;color:#4F81BD;line-height:2em;&quot;&gt;负了天下…又怎样&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;line-height:2em;&quot;&gt;！&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;text-align:center;color:#222222;font-family:Arial, Verdana, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;text-align:center;color:#222222;font-family:Arial, Verdana, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;span style=&quot;font-size:18px;color:#00B050;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;line-height:2em;&quot;&gt;人…不必太美，&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;text-align:center;col