E_D("replace into `imicms_img` values('177','6','1457963911','知识','1','找个，不骗你，不伤害你，和陪着你的人','11','生活小常识','http://wx.eake.cn/uploads/2/257e21c8a9e1be8c/1/0/3/d/thumb_545d7d78ed978.png','1','&lt;p&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;line-height:2em;color:#0070C0;&quot;&gt;&lt;span style=&quot;line-height:2em;background-color:#C3D69B;color:#3E3E3E;&quot;&gt;&lt;strong&gt;&lt;span&gt;【更多你喜欢的】&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;strong&gt;&lt;span&gt;点击标题上面蓝色字“&lt;span style=&quot;color:#0070C0;&quot;&gt;&lt;a href=&quot;http://mp.weixin.qq.com/s?__biz=MjM5NjYyNDU3MA==&amp;mid=201415213&amp;idx=1&amp;sn=b22d138667085247c432f403f46815c5#rd&quot; target=&quot;_blank&quot;&gt;杰诚生活&lt;/a&gt;&lt;/span&gt;”关注&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;div&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;line-height:2em;color:#0070C0;&quot;&gt;&lt;strong&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/div&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;img src=&quot;http://mmbiz.qpic.cn/mmbiz/BFobaMTWicLJBfrQc9bYXJhzib4zdTmIRsMwK6YtDqGmM4X3JtkCoZs8oH5xtGbx6IPFqFHENdMLqIPME8mtwuDQ/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;h2 class=&quot;rich_media_title&quot; id=&quot;activity-name&quot; style=&quot;font-size:20px;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;&quot;&gt;\r\n		找个，不骗你，不伤害你，和陪着你的人\r\n	&lt;/h2&gt;\r\n	&lt;div class=&quot;rich_media_meta_list&quot; style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;&quot;&gt;\r\n		2014-11-08&amp;nbsp;杰诚&amp;nbsp;&lt;a class=&quot;rich_media_meta link nickname&quot; id=&quot;post-user&quot;&gt;杰诚生活&lt;/a&gt;\r\n	&lt;/div&gt;\r\n	&lt;div id=&quot;page-content&quot; style=&quot;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;&quot;&gt;\r\n		&lt;div id=&quot;img-content&quot;&gt;\r\n			&lt;div class=&quot;rich_media_thumb&quot; id=&quot;media&quot; style=&quot;font-size:0px;&quot;&gt;\r\n				&lt;img src=&quot;http://mmbiz.qpic.cn/mmbiz/PZkvTNGf6ATpNgXjc8FBYpuVJNs6QKcSzI44VicbYV8T3mw7IhgtEJ0fyMWeh2W5lUsKTT7Du4hTQQ737G64c9A/0?tp=webp&quot; style=&quot;width:698px;&quot; /&gt;\r\n			&lt;/div&gt;\r\n			&lt;div class=&quot;rich_media_content&quot; id=&quot;js_content&quot; style=&quot;color:#3E3E3E;&quot;&gt;\r\n				&lt;p&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;正的富有，不是银行卡上的数字，而是你脸上的幸福的微笑。&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;img border=&quot;0&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/n8mCsvnxCghR5IicibEEwD8ADPmzS4AdjgMrS8RUL6fiauq8w9KbwxcVtosA17nMRx7Ec4oXsmYicSMzopZSI67C2Q/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;2. 在不对的时间，不对的地点，只要遇到了对的人，就一切都对了。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;img border=&quot;0&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/n8mCsvnxCghR5IicibEEwD8ADPmzS4AdjgH69AkuYPgialiczoM5DfjEqXEJicYv2icicUqXbhNlYcLUcUcqvc3HR8VPw/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;3. 对女人而言,钱不是重点,重点是每一个关键的时刻,你都能陪在她身边。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;img border=&quot;0&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/n8mCsvnxCghR5IicibEEwD8ADPmzS4Adjg0aEibfRaEGLzXLibSqnIoxbfP5jh5huh8Bzy7pGNcHicbyXIUgP6FEOtg/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;4. 世界上最幸福的三个字就是：在一起。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;img border=&quot;0&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/n8mCsvnxCghR5IicibEEwD8ADPmzS4AdjgN28gLDy1lXIicibq1DkypNiayaa4ArqCyaIsqyGdA2DKQLro7ICxIZvaw/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;5. 两个人相爱的证据就是不管在哪，彼此都在对方的眼里。爱情有时候就是这么简单。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;img border=&quot;0&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/n8mCsvnxCghR5IicibEEwD8ADPmzS4AdjgLviaCGg4Q3vfpY9MIic0lVqHmhibtYgoK1TIpLhiafPL35Q7ZjrlQzic7uw/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;6. 唯有你愿意去相信，才能得到你想相信的。对的人终究会遇上，美好的人终究会遇到，只要让自己足够美好。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;img border=&quot;0&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/n8mCsvnxCghR5IicibEEwD8ADPmzS4AdjgZlk365R0aFkKqbe6pBzITlYCvZTMwy4UIAia7RcPc6FJTFBXXOrj12A/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;7. 他也许不会带我去坐游艇吃法餐， 但是他可以每天早晨都为我跑几条街去买我最爱吃的豆浆油条。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;img border=&quot;0&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/n8mCsvnxCghR5IicibEEwD8ADPmzS4AdjgVyiawVGtiaMTSeesntiaia5t52DNHFOJZSAZC5QMMOf9Al8WwkVPJC8qoA/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;8. 钱多钱少不重要,重要的是找个知冷知热知心的好男人疼你。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;img border=&quot;0&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/n8mCsvnxCghR5IicibEEwD8ADPmzS4Adjg0Uynfp7J3eKfL3ibd5zUv0ryOibnpuRHyI3yib57na55NSyGzTXtPiczvA/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;9. 如果我们都住在对方的心里， 死亡就不是分离。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;img border=&quot;0&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/n8mCsvnxCghR5IicibEEwD8ADPmzS4AdjgtWeAWlsHiaEpSbWvtTj909ia0v1iacrMUakNWC2BWL97lzgO93iaDB5hqQ/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;10. 爱情不是依附，爱情是各自坚强独立，再努力走到一起。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;img border=&quot;0&quot; src=&quot;http://mmbiz.qpic.cn/mmbiz/n8mCsvnxCghR5IicibEEwD8ADPmzS4AdjglP9K8GO2YH0WFUI1oKHXbycd1mZuabZF7xGU1ecyWiashE5mAhgYiaug/0&quot; style=&quot;height:auto !important;width:auto !important;&quot; /&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;br /&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;编后语：&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;一个人就算再好，但不愿陪你走下去，那他就是过客。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;一个人再有缺点，但能处处忍让你，陪你到最后，就是终点。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;都想找个十全十美的爱人，可人总有缺点。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;爱情就是相互忍，彼此真，过一生。&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;找个让你一辈子开心的人，才是爱情的目标&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;所以，选爱人不需要太多标准，&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:宋体;font-size:21.33333396911621px;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;&lt;span style=&quot;color:#002060;&quot;&gt;只要这三样：不骗你，不伤害你，和陪着你&lt;/span&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:Tahoma, Helvetica, Arial, 微软雅黑, 华文细黑, 宋体, sans-serif;color:#444444;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;关注我们提供更多省钱攻略，有惊喜特价，温馨提示，各种当地服务！&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p style=&quot;font-family:Tahoma, Helvetica, Arial, 微软雅黑, 华文细黑, 宋体, sans-serif;color:#444444;background-color:#FFFFFF;&quot;&gt;\r\n					&lt;strong&gt;如何尽快找到我们呢？亲！&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;strong&gt;&lt;strong&gt;&lt;/strong&gt;&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;strong&gt;安宁杰诚家政服务&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;strong&gt;联系电话：0871-68681294&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;strong&gt;移动电话：13888384509 15198862744&lt;/strong&gt;\r\n				&lt;/p&gt;\r\n			&lt;/div&gt;\r\n		&lt;/div&gt;\r\n	&lt;/div&gt;\r\n&lt;/p&gt;','','1415413169','1415413169','1','257e21c8a9e1be8c','找个，不骗你，不伤害你，和陪着你的人','','177','0','0','0',NULL,'1','1','0','1','','');");
E_D("replace into `imicms_img` values('178','1','eake','其他','2','不管是自媒体，还是高级职业经理人，一个强大个人品牌，处处受益。这张图告诉你从A到Z个人品牌建设26条指南，帮你从屌丝变成业界有价值的人物！','18','其他营销','','1','&lt;p&gt;\r\n	不管是自媒体，还是高级职业经理人，一个强大个人品牌，处处受益。这张图告诉你从A到Z个人品牌建设26条指南，帮你从屌丝变成业界有价值的人物！\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;/tpl/Static/kindeditor/attached/99630ff411650cfa/image/20141108/99630ff411650cfa2014110812434921480.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;color:#7F7F7F;&quot;&gt;&lt;span style=&quot;color:#FF0000;&quot;&gt;&lt;span style=&quot;background-color:#000000;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;color:#64451D;&quot;&gt;如果您喜欢我们的内容请动动小手指转发到您的朋友圈&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#444444;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span&gt;&lt;b&gt;您还可以点击：&lt;a href=&quot;http://mp.weixin.qq.com/s?__biz=MzA3MTE4OTgyNg==&amp;amp;mid=201017818&amp;amp;idx=1&amp;amp;sn=d4fcf7d2a518d4b3fc1d4c4f971ffc21#rd&quot;&gt;亚篮网公众服务&lt;/a&gt;&amp;nbsp;&amp;nbsp;关注我们&lt;/b&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n&lt;/p&gt;','','1415421833','1415421879','1','99630ff411650cfa','26种方法，教你如何打造个人品牌','','172','0','0','0',NULL,'1','1','0','1','','');");
E_D("replace into `imicms_img` values('179','1','eake','其他','2','长久以来，媒介一直作为商业市场的守门人角色而存在。\r\n同时，由于媒介资源的稀缺性，企业的产品想要进入市场，就不得不支出高昂的买路费。但是能承担高昂买路费的基本上只有大企业。','18','其他营销','http://wx.eake.cn/uploads/9/99630ff411650cfa/5/6/5/2/thumb_545da005bca96.jpg','1','&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;长久以来，&lt;span style=&quot;font-weight:700;&quot;&gt;媒介一直作为商业市场的守门人角色而存在&lt;/span&gt;。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;同时，由于媒介资源的稀缺性，企业的产品想要进入市场，就不得不支出高昂的买路费。但是能承担高昂买路费的基本上只有大企业。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;因此传统的商业市场中，大企业通过“大预算”的确能垄断媒介资源，通过把持媒介资源来维持产品的市场表现。小企业即便有优质产品，由于缺乏预算，很难打开媒体把守的大门，导致快速上升空间相对有限。结果是，由于很难受到新产品挑战，传统商业市场中的旧产品生命期一般都比较长，产品更新换代速度较慢。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;所以传统市场的份额占比，往往呈现出按“预算能力”进行分配的特点，预算在市场总营销支出中所占比例越大，销售份额相应也会大些。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;但是这种情况正在扭转，媒介巨变带来了新的变动。市场的“媒介守门”定律成为过去，虽然当下的大企业依旧冥顽不灵的通过更大的预算试图尽可能的夺取注意力，但消费者对信息的选择有了主动性。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;购买决策不再限于广告信息，社交化、互动化的传播环境中，用户越来越容易获取他人的体验经验，这些体验经验正在成为决定购买的重要因素。&lt;/span&gt;从而给产品快速并大范围的“口碑传播”提供了良好土壤。从这方面看的话，产品口碑变得越来越重要，也就是产品本身的使用体验正在变得越来越重要。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;宝洁“重营销，轻产品”的作业方式无疑是非常好的例子，近几年中，宝洁每年百亿美元的营销支出，并未给他换来真正的市场增长，业绩一路下滑，利润逐年减低。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;因此，大企业还想用大预算垄断媒体资源来把持市场，已不是那么有效，营销不再是产品成败的决定因素。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;当然，这里需要说句题外话，在互联网的荒漠区域，传统作业还是有影响力的，尤其是中国三四线地域还未得到充分商业开发的情况下。因此，企业通过投放具有全覆盖率的央视系统，进行地毯式轰炸，的确还可以收获不错的效果。不然，京东、阿里巴巴等企业也不至于去乡郊野外“刷墙”。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;但需要认清形势的是，主流市场的风向已发生重大变化。&lt;span style=&quot;font-weight:700;&quot;&gt;媒介逐渐沦为基础服务，信息的解放使消费者变得越来越“聪明”，原本由媒介看门的传统商业市场，正在逐渐转向由用户（消费者）把门的新市场。&lt;/span&gt;企业，也需要逐渐由“搞定媒体”来搞定市场的做法，逐渐转向“搞定用户”来获得市场。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;全球著名的媒介资源出版商Google，无疑极为敏感，上半年，他在美国市场尝试为自己的产品列表广告（Product Listing Ads）增加了“产品评价信息”的新功能。Google的这种做法说明他们已经意识到改变的来临，并且尝试作出探索。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;Google的产品列表广告增加了评价信息后，被搜索到的产品会以比较表格的形式展示，每个产品下方会出现评级信息，同时还有该产品的被点评数。通过这些信息，用户可以直观的进行产品间的横向对比，有助于用户做出购买决策。当然，我更愿意将这些“评价信息”称为“第三方用户体验经验”。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;问题是，这种方法是否有效。答案有些模棱两可。不过，有一点是可以确认的，通过“他人的产品或服务体验经验”来参考购买决策，本质上属于“口碑传播”，而“口碑传播”其实是“去广告化”的，在现有的广告行业中，广告商无法运用现有的商业逻辑，在这个链条中直接赚取广告方面的受益。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;原因大致有三个。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;1、&lt;span style=&quot;font-weight:700;&quot;&gt;“口碑效应”本是自发行为，广告商除了去推动它，中间无法像传统广告那样进行产品修饰和品牌修饰。&lt;/span&gt;因为，如果广告商参与产品修饰和包装，那么对用户而言，这些体验经验就失去了纯粹的第三方意义，也就无所谓“口碑”这两个字了。很可能是，口碑传播会被中止，或污染。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;那么，广告商（包括出版方和代理方）如果在这个过程中无所作为，那么凭什么去赚企业的钱。另一方面，媒体也无法通过惩罚用户来获得盈利门槛。媒介沦为免费服务的当下，用户帮优质产品进行口碑传播，媒体无法阻止或扭曲这种传播行为。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;那么媒体真的无所作为吗？其实可以有，媒体可以去推动口碑传播的发生，但从这个角度出发的话，这种价值目前还很难量化。微博作为口碑营销的核心战场，之所以到目前为止依旧无法盈利，就是因为口碑传播因为自发性和中立性的特点，导致微博主体很难插足，即便尝试去推动口碑传播的形成，但其中商业价值的量化也十分困难。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;2、假设Google试图通过“评价信息（口碑）”来提高某产品的转化率，势必会得罪竞品企业，等于搬起石头砸自己的脚，难道他就不赚其他竞品企业的钱了？&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;3、即便“口碑效应”扩大过程中没有竞品参与（这种可能性很小，口碑的产生往往是通过竞品对比为出发点的），企业出钱的意愿也会减少。因为口碑传播本身，往往不是媒介主体自身能完全掌控的，其自发性和不确定性，很难让品牌认为这是媒介本身的功劳，大有一种为他人作嫁衣裳的无力感。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;因此，整个传播环境趋向于社交化、互动性的当下，当“在线口碑”变得越来越重要，当企业逐渐开始将主要精力放在产品本身，那么，传统营销（也可以理解为传统的广告商业）的商业价值的低洼地被慢慢填平了，其核心的广告商业被破坏殆尽。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;至少目前来看，对传统营销而言，在目前的传播环境下，而且这种社交化、互动性的传播环境还会越来越强，营销的“去商业化”苗头是比较明显的，如果不作出革新，营销业的盘子出现萎缩似乎可以预见。而Google增加评价信息的做法，虽然顺势时势，变相在为口碑在线化进一步铺平道路，其实是在革自己广告商业的命。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;这是好事。或许在未来，Google也并非要靠广告赚钱，有了用户，就有了一切的可能。而且，商业模式是不断向前进化的。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;当然，就目前来说，营销的新商业模型的确还未出现。广告商业，曾经是以“手工艺”的“包装”为核心，我帮你包装的越好才能传播越广，广告费就收得越多。那么现在，广告商业似乎唯一能做的是，如何帮企业更快速的形成“口碑”。广告的作用似乎减弱了，产品本身的体验变得越来越具有决定性。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;不过，现实还没有那么悲观。虽然，现在很多大企业依旧在增加预算的量，传统营销盘子似乎还在增长，但他们会慢慢发现，海量曝光并不是产品打开市场的决定因素，营销也不需要依靠海量的预算来支撑，这个现实会变得越来越清晰。这种情况下，营销盘子萎缩的转折点，的确可以预见。但是，新的营销价值观念却正在形成。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;一个很有趣的趋势：毫无疑问，企业通过专注产品的体验，来掌控市场这个逻辑越来越趋向于称为“真理“，但企业的成长过程中，非常关键的一环也同时出让给了市场，即，如何在“在线传播环境”中形成海量的正面的产品体验经验。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;这些散落在微博、朋友圈、豆瓣、知乎、果壳等社区站点的“点评信息”，如何能够“更容易”到达用户这里，成为决策购买的重要因素。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;市场上会不会出现一款产品，帮助企业，或者帮助用户，把这些散落在全网各处的“经验”输送到有需要的地方，而不是把粗暴的广告营销信息，直接输送到消费者手里。让更客观的UGC说话似乎更有说服力，而不是自认为专业的PGC内容。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;那么对营销而言，重点就变成了，如何组织和把控这些客观的用户体验经验，如何把握用户对产品和服务的吐槽。&lt;/span&gt;其实，只要能够把持全网层面上的这些“用户经验”，那么就能在未来的营销中掌握主动。因为他们比任何营销信息都有说服力。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;而在新的商业市场，基于完全社交化的在线环境，随着产品本身变得越来越重要，随着用户能更容易的识别哪些产品更符合需求，小企业的产品和服务在市场上有了更大的可能性，产品和服务的更新换代速度会越来越快。这也是为何宝洁的营销越来越失效的原因，也是三星的广告轰炸无法给他带来持续的市场效应的原因。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;至于小米的崛起，也是这样的原因。当然，小米的用户体验不算好，不过对于“用户体验”这个词的概念，更应该包含另一层含义，低廉的价格，也是其重要的组成部分，而且是不可或缺的部分，这是互联网时代社交化市场的重要内涵。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;color:#7F7F7F;&quot;&gt;&lt;span style=&quot;color:#FF0000;&quot;&gt;&lt;span style=&quot;background-color:#000000;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;color:#64451D;&quot;&gt;如果您喜欢我们的内容请动动小手指转发到您的朋友圈&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#444444;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span&gt;&lt;b&gt;您还可以点击：&lt;a href=&quot;http://mp.weixin.qq.com/s?__biz=MzA3MTE4OTgyNg==&amp;amp;mid=201017818&amp;amp;idx=1&amp;amp;sn=d4fcf7d2a518d4b3fc1d4c4f971ffc21#rd&quot;&gt;亚篮网公众服务&lt;/a&gt;&amp;nbsp;&amp;nbsp;关注我们&lt;/b&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;','','1415421963','1415421963','0','99630ff411650cfa','互联网时代 口碑传播会取代广告营销吗？','','179','0','0','0',NULL,'1','1','0','1','','');");
E_D("replace into `imicms_img` values('180','1','eake','其他','2','不少卖家都有打造品牌的意识，但对于中小卖家来说，如何系统化的操作一个品牌始终是一头雾水。“品牌”二字太虚无缥缈，以销售为指标的中小卖家更多关注的是推广、运营等实操方面的事情，如何系统化的强化店铺品牌形象，对于卖家长久发展，可持续运营来说不可忽视。','18','社会及其他营销','','0','&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;不少卖家都有打造品牌的意识，但对于中小卖家来说，如何系统化的操作一个品牌始终是一头雾水。“品牌”二字太虚无缥缈，以销售为指标的中小卖家更多关注的是推广、运营等实操方面的事情，如何系统化的强化店铺品牌形象，对于卖家长久发展，可持续运营来说不可忽视。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;在品牌影响下，消费者愿意为品牌溢价买单，同时更愿意在社交媒体展示产品。作为中小卖家，没有专业的品牌管理人员和经验，如何做自己的品牌？可以通过4个方面做一些简单规划——&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;Step1：定位自己&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;建议尤其在红海行业的中小卖家，找准自己产品定位和品牌定位。如之前提到的紫魅，从紫色这个细分市场入手，而小黑伞就是从爱好英伦风情的目标客户入手。找到一个点，把它发挥到极致，才有做品牌的基础。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;Step2：重视门脸&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;在影响消费者做出购物决策中，会有“第一眼效应”。消费者初次到一个不太了解的店铺，第一次看到页面后就会形成的一种直观感觉。由于它有先入为主的特点，因而往往比较深刻。如果第一印象好，在会这种印象的影响下，他对这个店铺或商品的其他特性也会给予较好的评价。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;淘宝上有一家专卖四川土特产的店铺“把文翰的食材店”。产品单价高于市场价20~30%，但是购买和评价就看得出这家店铺给人的印象。包装很好”“很舒服”是提及率较高的两个评价。这家店铺装修是极简风格，而产品详情是这个食材的“故事”。一瓶小小的豆瓣酱，从第一代传人再到制作工艺，给人一种我不是在买东西，而是在浏览一部历史书的感觉。产品包装简单而又充满一种人文情怀，从店铺装饰、产品包装都看得出这家店主人涵养和理想主义，充满情意的产品介绍和图片，更让消费者愿意为其品牌溢价而付费。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;门脸的重要性不言而喻。建议大家找到相对专业的设计师，设计一套店铺或者品牌VI。店铺的装修、产品的外包装、快递的包装等保持统一的视觉形象。这样在同消费者的三次沟通中(首次入店浏览→商品详情页浏览&amp;amp;售前咨询→交易完成)，店铺或者产品的品牌形象至少会在消费者的心智中加深三次以上的印象。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;Step3：借助“柔性”传播力&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;上文的“把文翰”把自己寻找食材的过程放在自己的微博上。通过一段又一段食材的寻找，同消费者进行了最直接的沟通。一般的卖家会说，自己没有时间维护一个店铺或者品牌微博。那么请抓住售前和售后咨询的机会，加深用户品牌记忆，客服需注意使用统一话术。客服人员需对自己的产品及特性完全了解。在同消费者的沟通中，不断强化品牌。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;以下为目前卖家常用定位策略及VI和“软传播”策略:&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-align:center;&quot;&gt;\r\n	&lt;img src=&quot;/tpl/Static/kindeditor/attached/99630ff411650cfa/image/20141108/99630ff411650cfa2014110812471260256.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;Step4：学会讲故事&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;讲故事，这种能力比较考验店主或者商家的运营能力。人们会通过微博微信搜索或受意见领袖的影响，选择商品。在人人都是传播者的年代，讲一个好的故事，往往会收获到不一样的东西。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;最佳例子：褚橙。褚时健的人生经验给普通水果橙子打上了“不服输”的印记。即使比普通橙子贵出30~50%，仍旧一抢而空。说故事的前提是热爱所销售产品的一切。把文翰店铺，一瓶豆瓣酱讲述了一段家族的兴衰史；一瓶蜂蜜，讲出了自己对父亲的爱；一把笋，讲出了挖笋人的辛劳。而在他的个人微博上更是直播了美式寻觅之旅。不需要多么动情的文字，真的热爱才能触动人心。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;讲故事的另外一个技能，是熟练使用一些工具。微博微信豆瓣平台，及时回复和互动，成体系的内容规划。一三五可以是店铺上新，二四六可以讲讲品牌故事或者互动。在社交媒体互动的时候，建议保持店铺一样的风格。在互动中，一次又一次的传递品牌的DNA。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;不需要多么复杂的手段来打造品牌。给自己的店铺一个准确的定位，在保证产品质量情况下，给消费者讲一个充满血肉的故事。统一风格的店铺装饰、客服话术、社交媒体运作等。在一切能同消费者互动的环境都尽力互动，相信在一次又一次的互动中，收获的不仅是交易额，更是一个深入人心的品牌形象。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt; &lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;color:#7F7F7F;&quot;&gt;&lt;span style=&quot;color:#FF0000;&quot;&gt;&lt;span style=&quot;background-color:#000000;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;color:#64451D;&quot;&gt;如果您喜欢我们的内容请动动小手指转发到您的朋友圈&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;b&gt;您还可以点击：&lt;a href=&quot;http://mp.weixin.qq.com/s?__biz=MzA3MTE4OTgyNg==&amp;amp;mid=201017818&amp;amp;idx=1&amp;amp;sn=d4fcf7d2a518d4b3fc1d4c4f971ffc21#rd&quot;&gt;亚篮网公众服务&lt;/a&gt;&amp;nbsp;&amp;nbsp;关注我们&lt;/b&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;','','1415422074','1415425451','0','99630ff411650cfa','淘宝中小卖家品牌塑造法','','180','0','0','0',NULL,'1','1','0','1','','');");
E_D("replace into `imicms_img` values('181','1','eake','其他','2','闺蜜可以说是熟人中的熟人，在线在大谈熟人经济的时代，闺蜜的营销质量算是最高的。当你想说服女人掏钱购买东西时，朋友推荐才是最好的方式','18','社会及其他营销','','0','&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;span&gt;闺蜜可以说是熟人中的熟人，在线在大谈熟人经济的时代，闺蜜的营销质量算是最高的。当你想说服女人掏钱购买东西时，朋友推荐才是最好的方式。Ladies’ Home Journal近期发起了一项调研，得出女性消费者会更多考虑朋友间的建议而非其他形式的商业广告。大多数受访者也表示熟人的购买经验能直接影响他们的购买决定。&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;/tpl/Static/kindeditor/attached/99630ff411650cfa/image/20141108/99630ff411650cfa2014110812493794185.jpg&quot; alt=&quot;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;color:#7F7F7F;&quot;&gt;&lt;span style=&quot;color:#FF0000;&quot;&gt;&lt;span style=&quot;background-color:#000000;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;color:#64451D;&quot;&gt;如果您喜欢我们的内容请动动小手指转发到您的朋友圈&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;b&gt;您还可以点击：&lt;a href=&quot;http://mp.weixin.qq.com/s?__biz=MzA3MTE4OTgyNg==&amp;amp;mid=201017818&amp;amp;idx=1&amp;amp;sn=d4fcf7d2a518d4b3fc1d4c4f971ffc21#rd&quot;&gt;亚篮网公众服务&lt;/a&gt;&amp;nbsp;&amp;nbsp;关注我们&lt;/b&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;div&gt;\r\n	&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;br /&gt;','','1415422208','1415425460','3','99630ff411650cfa','闺蜜的口碑营销：当闺蜜“枕边风”完胜商业广告','','181','0','0','0',NULL,'1','1','0','1','','');");
E_D("replace into `imicms_img` values('182','1','eake','其他','2','你必须专注一个点才能极致，极致了之后才能有口碑，所以第一个一定是极致到超用户预期。像红米note手机就很爆炸，那么大的屏幕，和三星的Note一样大，就卖799，本身的超用户预期。\r\n','18','其他营销','http://wx.eake.cn/uploads/9/99630ff411650cfa/6/c/1/b/thumb_54534fb81db10.jpg','0','&lt;table cellspacing=&quot;0&quot; cellpadding=&quot;0&quot; style=&quot;width:960px;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:12px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td class=&quot;t_f&quot; id=&quot;postmessage_5943&quot; style=&quot;font-size:14px;&quot;&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;一，极致的产品或者服务&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　你必须专注一个点才能极致，极致了之后才能有口碑，所以第一个一定是极致到超用户预期。像红米note手机就很爆炸，那么大的屏幕，和三星的Note一样大，就卖799，本身的超用户预期。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　我觉得拉勾网之所以过去几个月做的好，24小时极速入职第一个爆发点。我们会发现，比如说京东物流是一日两送，上午九点之前订货，下午四点之前送到，所以大部分人第一次收到这么快的物品挺惊讶的，这就超越了预期。我们就琢磨，找工作也一样，投简历石沉大海，可能过一两星期才接到面试电话。那么投简历为什么不能像买东西一样那么快得到回复?今天中午十二点之前投简历，晚上十二点之前一定会得到回复，告诉你简历有没有通过，能不能得到面试。晚上十二点之前投的简历，第二天十二点之前一定给你回复。所以说整个口碑传的非常好，用户有很大的增加。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　比如说电商网站做物流时，会做物流的跟踪，就是你可以实时查看你购买的物品被送到什么地方了，用户觉得心里踏实。为什么投简历不可以知道我的简历有没有被HR收到，有没有被HR看到，HR现在是什么样的心理状态，要不要约我面试。我们就在拉勾网的微信公众账号里做这个事情，比如说你投简历过去之后，就会提示你，HR正在筛选你的简历，如果HR觉得简历还不错，就可以直接在微信上约你面试，这时候你就会受到反馈说，HR正在约你面试。这些都是实时的，整个反馈，从投递简历到收到面试通知，可能只要一分钟，这就会带来惊喜，带来口碑。我们自己的员工全部是拉勾网自己招的，有一个典型的案例，有一天夜里一个同学投递拉勾网的职位，拉勾CEO马德龙恰好还没睡，正在看微信，看到投递简历通知后，他马上在微信里查看简历，觉得还不错，就马上回信息给人家，明天早上九点半请你喝咖啡。大半夜两点钟的时候，那个同学微信一分钟之后收到一个回复说，明天早上九点半约你喝咖啡，他以为是系统在玩弄他，但是他大半夜不敢给马德龙打电话，就忍着。第二天一大早，他就打电话给马德龙，因为微信面试通知下面有电话，他说我昨天晚上两点钟收到一个信息，约我喝咖啡，是你们的信息自动回复吗?然后马德龙说不是，是真的喝咖啡，他就被惊呆了。然后就来拉勾网上班了。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;二、出其不意，不按常理出牌&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　大家都觉得皇太极煎饼好吃，但是他的味道真的超越了你的预期吗?我觉得大部分人是抱着超高预期去吃的，反而没有人说吃完了特别好吃的。如果说东西本身做不到超预期，就做到和大家脑袋里想的不一样，我觉得就是出其不意。大家觉得一个煎饼本来很地段的，怎么做得高大上，吃煎饼、喝豆腐脑还能思考人生，煎饼和思考人生到一起?这个和我平常想不一样，一旦出其不意了，一定会有一些效果，这个就是体验上的效果。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　有一个APP叫我要当学霸，是一个大学生团队做的，是腾讯以前COO曾李青投资的项目。因为大部分的大学生总是忍不住玩手机，他一方面要戒手机，一方面又戒不了。所以90后这种自虐心态很强，所以他们就做一个我要当学霸。设置功能我要两个小时不玩手机，设完之后这个app就把他的手机锁了，所有的程序都打不开，只能出现这一个界面，你如果尝试着去干其他的事情，他就提示你，他就鄙视你，说很难听的话给你。比如你设了晚上十二点钟要睡觉，十二点钟不睡就会让你睡，你还不睡就会出来这样的信息，没有其他的按纽，只有我错了，不按“我错了”你过不去。但是你发现他功能上一定超越体验了吗?其实没有。很简单，就是说他的方式和大部分的APP不一样，大部分的是充分的尊重用户怎么样，但是他就是虐待你。这是四个人的大学生团队，几乎没有花什么营销费用，但是在大学的用户群的渗透率已经很高了。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　所以我觉得口碑产生归纳起来就是两个，东西本身做得好，第二是如果东西本身很难做得好，就做得出其不意，和别人想得不一样。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　口碑产生了之后怎么传播出去?&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　一、要给用户一个传播的理由&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　第一个理由是对朋友有用，比如说拉勾网做了一个快速涨身价的攻略，就是告诉一个做互联网的人，做技术的怎么涨身价，你是做产品的怎么涨身价，讲了很多的技巧。这样用户就会转这个信息，因为转完了之后觉得对朋友有用。你看大家最多转发的是八条身体健康指引、五种植物让你健康之类的，都是一个量化的，好像有好处的东西，所以这是第一条，对朋友有用的。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　第二个，显得自己很怎么样。比如说很多女孩儿会说，又去巴黎买了一个包。又有人说，坐在香格里拉楼顶咖啡厅，觉得自己很孤单。但是最高境界是调动情绪，比如说有一篇文章说，这个爸爸从一岁照他女儿的照片照到十四岁罗列起来，或者说讲妈妈怎么样含辛茹苦把他养大，或者小动物怎么可怜，最终调动这个人的情绪，自己就融入进去的，看到这个想到自己的妈妈，想到社会爱心很感动，这是情绪。情绪的时候就不理智了，不理智就会更无约束的帮你转发你的信息、传播你的信息，而且加上很有感情的语言，有感情的语言是最感染别人的。如果帮你转播的人把情绪、感情带进去了，他的朋友看到把情绪、感情带进去转播的东西，这比干巴巴的三个更健康的信息的传播性更强一点。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　第三个，其实可以给自己制造一些负面。比如说拉勾网现在做的事情，如果公司发职位，发的职位工资低于多少，就不允许发，他们说你们拉勾网变态，发了三千多块的工资还说太小气了，不让我发这个职位，骂我们的时候，其实对我们就是一个宣传，让大家来这儿三千以下工资的职位还不让发，拉勾网都是高工资的职位。曾经我们自己打算投一个项目是做相亲的，因为现在做相亲行业的事情特别难做，主要是每一个相亲活动美女特别多，找不到男的。但是你说男的不想找女朋友吗?也不是，说男的不好色吗?也不是。所以我们就说做一个相亲会的目标不能让所有的人都来，比如说低于1.6米的女生不能进，每个女神来必须三个男生面试，不够漂亮不让进，就设置严格的坎儿，这个时候有一个女生跑过来，我们就说你太丑了，我们不让进，这个女的就特别特别生气，你说我丑，她就会到处骂你，见任何机会都骂你，说我丑不让我进。但是这种话说出去之后，所有男生之后都对这个组织充满向往，都会觉得丑的人不让进，这个组织太牛了。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　二、给用户一个传播的场景&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　要创造一个传播口碑的场景，东西本身很好，要给用户创造场景让他告诉别人你很好。我跟大朴网的CEO是好朋友，有一次聊过怎么做传播。他们做纺织品，都是无甲醛、纯棉的，所以说他的用户就是高级白领、妈妈或者女孩儿。用户买了之后很好，但是不至于女孩主动在办公室告诉别人，我在网上买了一个内衣。没有理由说，怎么样说?比如说大朴做电梯广告，我问他为什么?他说首先电梯里放广告的标准是这个小区里曾经有人买过他的东西。如果电梯里有这个广告，就给买过他东西的人一个场景，在他坐电梯的时候，可以给周围的朋友说，这个我买过，还不错。你说这句话的时候，电梯里的所有人可以听到，这就是给一个场景。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　第二个场景是，一个女孩儿买了毛巾也好，内衣也好，怎么在办公室里说?他就说所有的商品开始全部都放二维码，盒子里有二维码，用户买到东西打开的瞬间，就是他把他的东西传播给办公室其他人的场景。所以就创造了办公室里所有的包装都有二维码，打开之后能告诉别人好，而且周围人能够瞬间扫描它的二维码，能够进入他的网站买东西，这就是创造的场景。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　我总结一下，第一个，创造口碑，东西要好，超用户的预期，超不了预期就出其不意。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　传播就是第一要对朋友有用;第二要你要让他觉得他很怎么样;第三可以给自己创造一些负面信息，负面信息一定程度上说，如果只服务一部分人，我要牺牲掉其他一部分人，严格的和一部分人打架，这部分人天天骂你成了你的口碑。东西好了，要给用户创造一个场景说你好，不然他没有道理说你好，所以我的分享就这么多。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;　　核心的就是说，要有粉丝，最终才能是一个品牌。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n				&lt;/p&gt;\r\n				&lt;p&gt;\r\n					&lt;span&gt;&lt;span&gt;&lt;span&gt;\r\n					&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n						&lt;strong&gt;&lt;span style=&quot;color:#7F7F7F;&quot;&gt;&lt;span style=&quot;color:#FF0000;&quot;&gt;&lt;span style=&quot;background-color:#000000;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;color:#64451D;&quot;&gt;如果您喜欢我们的内容请动动小手指转发到您的朋友圈&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;\r\n					&lt;/p&gt;\r\n					&lt;p style=&quot;color:#444444;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n						&lt;span&gt;&lt;b&gt;您还可以点击：&lt;a href=&quot;http://mp.weixin.qq.com/s?__biz=MzA3MTE4OTgyNg==&amp;amp;mid=201017818&amp;amp;idx=1&amp;amp;sn=d4fcf7d2a518d4b3fc1d4c4f971ffc21#rd&quot;&gt;亚篮网公众服务&lt;/a&gt;&amp;nbsp;&amp;nbsp;关注我们&lt;/b&gt;&lt;/span&gt;\r\n					&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n				&lt;/p&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;','','1415422445','1415422460','0','99630ff411650cfa','怎样用零广告费打造一个强品牌 ','','182','0','0','0',NULL,'1','1','0','1','','');");
E_D("replace into `imicms_img` values('183','1','eake','其他','2','在营销领域有被业界广泛提及，甚至很多门外汉都挂在嘴边标榜的两种策略，那就是饥饿营销与口碑营销，在贾春宝看来，这与道貌岸然的人讲“慈善”，斤斤计较的人讲“舍得”，急功近利的人讲“战略”，市场强盗讲资源“整合”没什么差异。假如单纯说说也就罢了，执行起来就会呈现“找死”的节奏。','18','社会及其他营销','http://wx.eake.cn/tpl/Static/kindeditor/attached/99630ff411650cfa/image/20141108/99630ff411650cfa2014110812563791026.jpg','1','&lt;p&gt;\r\n	&lt;span style=&quot;color:#454545;font-family:微软雅黑;font-size:medium;line-height:28px;background-color:#FFFFFF;&quot;&gt;在营销领域有被业界广泛提及，甚至很多门外汉都挂在嘴边标榜的两种策略，那就是饥饿营销与口碑营销，在贾春宝看来，这与道貌岸然的人讲“慈善”，斤斤计较的人讲“舍得”，急功近利的人讲“战略”，市场强盗讲资源“整合”没什么差异。假如单纯说说也就罢了，执行起来就会呈现“找死”的节奏。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#454545;font-family:微软雅黑;font-size:medium;line-height:28px;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt; &lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;div align=&quot;center&quot;&gt;\r\n	&lt;img src=&quot;/tpl/Static/kindeditor/attached/99630ff411650cfa/image/20141108/99630ff411650cfa2014110812563791026.jpg&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&lt;/div&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　在实际制订市场谋略的时候，我们总是要遵循一定的常识与基本的逻辑思维。而任何营销都是谋略，是在缜密的逻辑思考的基础上，经过无数次提出之后被推翻，然后再如小强一般具有生命力的，都是需要有极为理性支撑的。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　不管是饥饿营销还是口碑营销，核心都不是理性诉求而是感性诉求，假如你让在其中的参与者对所享受到的快感和恐慌的程度有一个量化与具体描述，那么ta是根本说不上来的，因为那本身就是难以言表、难以复制、经不起推敲的个性化感受。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　任何感性的表达与传播，都建立在理性的基础之上，否则就是沙漠上的宫殿与堡垒，不仅长期看无法阻止下陷并被沙子“吞蚀”，而且在短期之内随时会被风浪击碎的。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　遗憾的是，我们常规所说的饥饿营销，不过是拳头产品与核心服务的试用装，是在企业战略中被用做先锋与敢死队的，实际上与战争中的炮灰无异。而指挥官的整体谋略，或许就需要在这一些有生力量打光了，摸到了市场的脉搏之后，真正的拳头产品与主力部队才会以截然相反的策略推向市场。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　&lt;span style=&quot;font-weight:700;&quot;&gt;饥饿营销也是需要具备基本要素的，在这些不能具备的前提下，就是死路一条。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　&lt;span style=&quot;font-weight:700;&quot;&gt;　符合市场趋势。&lt;/span&gt;在全民崇尚虚华浮躁，都过度包装并夸夸其谈的时候，你在简朴方面做到极致，就会被抛弃;相反当全社会都鄙视奢华浪费，都在倡导清廉并在享受方面如履薄冰的时候，你偏偏要推出奢侈品与帝王般的享受，以奇高的价格标榜自己的高品质，这就是逆市场潮流而动，是寻死的策略了。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　在制订市场谋略的时候，大的金融环境、政策环境、产业与行业现状，甚至供应链与舆论导向等因素都是需要深入研究的基础功课，这些基础功课没做好就瞎指挥，能取得成功才是咄咄怪事。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　&lt;span style=&quot;font-weight:700;&quot;&gt;独特的定位&lt;/span&gt;，你所提供的绝对不能是模棱两可的东西，绝对不能跨界，让人感觉你是“四不像”你就完了。你需要找到市场空白，在特定消费人群的痛点上做文章，即使是在细微的点上，以偏执狂的境界去做，要做就做到极致，不管是在设计环节、包装环节，品控环节还是在市场营销环节，都需要有苛严的要求。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　&lt;span style=&quot;font-weight:700;&quot;&gt;做到极致的品质&lt;/span&gt;，至少专业人士都对你的品质有所认同，拥有一些对手想拥有，想模仿却又偷不走的东西，那是你所打造的优势资源，也是为自己营造的市场壁垒，同样是取得客户认同的核心标志。你需要得到权威认证，不单纯是“赞不绝口”，还要以公认的方式确定下来。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　&lt;span style=&quot;font-weight:700;&quot;&gt;去货币化的门槛。&lt;/span&gt;当你的产品具有独特性，你就有权利选择自己的消费者，对他们提出基本的硬性要求，为了显出难以取代的独特的气质，需要为自己的领域设定进入门槛，绝对不允许任何不合条件的蒙混过关，在客户体验方面，也需要用挑剔的标准来要求。同时必须注意，那并不是以金钱这个数量为评判标准的。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　财务数据是最直观，也是最害人的。当你设定的标准，假如是以财务数据与规模作为核心的，那么除了被鄙夷被抛弃，什么都得不到。在财务标准执行的过程中，有钱人觉得是包养了你，不会给你基本尊重与话语权，没钱的人觉得你势利眼，由于你的肤浅也会远离你。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　&lt;span style=&quot;font-weight:700;&quot;&gt;没有争议的代言人(与机构)。&lt;/span&gt;你的供应量可以少，但必须能有人推崇。所以你必须有明星级别的代言，或许那个代言人没有广泛的群众基础，但却是一种健康阳光、身价与地位的象征，成为市场粉丝的偶像与教父级别的人。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;　　粉丝的拥趸。&lt;/span&gt;没有脑残粉还想玩饥饿营销，那是自取灭亡。网络时代流行一个说法“脑残粉”，含义是“即使被嘲笑为脑残，都要去成为粉丝”，脑残粉会在市场中迸发难以想像的力量。由粉丝之类的忠诚拥趸所形成的支撑是生命力的源泉，那需要互相尊重彼此珍惜。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　饥饿从来都不是理性的东西，从感觉到饥饿到由于饥饿而死的整个过程，理性仅仅是在生命终结的那一瞬间，其他的时候都是在贪婪与恐慌的心理状态中，而这些心理状态所表现出来的基本都是感性的。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　&lt;span style=&quot;font-weight:700;&quot;&gt;　渠道是支柱。&lt;/span&gt;一本万利的逻辑只适合投机性的市场领域，而且具有很强的时效性，你需要有广泛的市场传播，让从同行业的从业者到分销渠道，街头巷尾都知道你的名号，即使你的身影不在，都不用担心被人忘记。你所卖出的高价之中注定有难以忽视的成本，而在很多时候，针对渠道、同行与行业性的专业媒体机构的传播更重要。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　&lt;span style=&quot;font-weight:700;&quot;&gt;　切记——不要用特供的方式把自己孤立起来，那是自绝地气的孤岛思维，当你做成了特供的东西，没有人会到陋巷之中去苦苦寻觅。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　&lt;span style=&quot;font-weight:700;&quot;&gt;　最后说说口碑营销。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　其实口碑营销是最奢侈的，也是最指望不上的。因为任何口碑效果都建立在不容忽视的巨大投入之基础上的，有很多投入都是你所看不到的，而且口碑营销通常仅仅可以是众多营销策略中的一个微小部分。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　口碑是谁都可以说，但却很少有人真正做到的，因为口碑意味着被试用试驾试吃试品试用，而所有的“试”都是免费的，都是不需要花钱的。而在那之前的精准客户的寻觅到重点客户的关系维护，从免费到付费，从无价到天价，这中间有漫长的道路要走。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　在这个已经高度发达的商品经济时代，任何资源、商品、服务都具有可替代性，甚至你跟不上更新换代的节奏，都会导致客户流失。所以那些靠粉丝生存的，不管是苹果还是小米，不管是文体明星还是学者作家，如果中断了炒作，最终只有落得个被边缘化被遗忘的结果。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　你用什么策略解决市场通路问题?解决从虚拟的意淫到可以被尝试并触手可及的问题，从浅尝辄止到上瘾并欲罢不能的问题，解决那些尝试过的人的心理诱因问题，确保他们不会单纯是贪图小便宜，而会由于吃人嘴短心理，为你鼓吹呢?&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　当然，ta们为你鼓吹之后，你如何建立健全那种“鼓吹”的苦劳与市场收益的“功劳”之间的价值评估以及论功行赏的激励机制?这是维持口碑营销的润滑剂，是不可或缺的。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　池塘越小所需要耗费的成本越低，但也仅仅是市场尝试，在尝试中所得到的抽样调查结果，注定难以形成指挥规模化市场所需要的市场智慧。能在自家泳池中畅游的人很多，但那样的经验照搬到陌生水域以及浩瀚的江河与海洋中，性命之忧是注定的。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;　　市场是用脚来投票的，除非你提供的是让人上瘾并已经欲罢不能的产品与服务，否则没有消费者愿意苦苦追随。当你单纯基于对某些谋略的一知半解就冒然以市场去尝试并验证，不管是让自己落伍还是过于乐观，都会遭到市场的封杀，所以，口碑营销是以精准的目标和持续性的创新为基础策略的。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;color:#7F7F7F;&quot;&gt;&lt;span style=&quot;color:#FF0000;&quot;&gt;&lt;span style=&quot;background-color:#000000;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;color:#64451D;&quot;&gt;如果您喜欢我们的内容请动动小手指转发到您的朋友圈&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span&gt;&lt;b&gt;您还可以点击：&lt;a href=&quot;http://mp.weixin.qq.com/s?__biz=MzA3MTE4OTgyNg==&amp;amp;mid=201017818&amp;amp;idx=1&amp;amp;sn=d4fcf7d2a518d4b3fc1d4c4f971ffc21#rd&quot;&gt;亚篮网公众服务&lt;/a&gt;&amp;nbsp;&amp;nbsp;关注我们&lt;/b&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;','','1415422629','1415425267','1','99630ff411650cfa','浅谈新手从饥饿营销到口碑营销','','183','0','0','0',NULL,'1','1','0','1','','');");
E_D("replace into `imicms_img` values('184','1','eake','其他','2','和其他品牌一样，H&amp;M在Facebook、Twitter、Pinterest等社交平台上均开设了官方账号，发布新品信息和时尚资讯，开展线上线下互动，回答粉丝提问等。随着越来越多的品牌进入这些平台，H&amp;M的优势不再显著。因此，在继续运营好已有平台的同时，H&amp;M积极发展新路径，在优势平台上着重发力，例如Google+。\r\n','18','社会及其他营销','http://wx.eake.cn/uploads/9/99630ff411650cfa/c/7/0/b/thumb_545da3813e887.jpg','1','&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;和其他品牌一样，H&amp;amp;M在Facebook、Twitter、Pinterest等社交平台上均开设了官方账号，发布新品信息和时尚资讯，开展线上线下互动，回答粉丝提问等。随着越来越多的品牌进入这些平台，H&amp;amp;M的优势不再显著。因此，在继续运营好已有平台的同时，H&amp;amp;M积极发展新路径，在优势平台上着重发力，例如Google+。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;在Google+上，最受欢迎的品牌既不是可口可乐、迪斯尼或星巴克(三者是Facebook上的热门品牌)，也不是谷歌自己。目前在Google+品牌关注度榜单中，H&amp;amp;M位列第三，远高于在Facebook和Twitter的表现。Google+因其“圈子”功能而有异于其他社交平台，这就需要品牌与粉丝进行更深入的互动，同时营销人员也能根据不同圈子的表现获得实时数据。H&amp;amp;M正是看到Google+的独特优势，希望在此平台上“与处在任何地方的目标消费者对话，分享最新时尚资讯”。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;首先，H&amp;amp;M将Google+的内容定位为富有灵感的、独特的。&lt;/span&gt;正如H&amp;amp;M的全球社交媒体经理Miriam Tappert所说，图像是传播时尚和生活讯息最有效的方式，H&amp;amp;M在Google+分享的内容多以图片、视频为主，并保证内容的新鲜度、即时性以及相关性。纵观H&amp;amp;M的Google+主页，精挑细选过的美图和视频给人赏心悦目的感觉，带来艺术和审美上的灵感触发。为了让粉丝获得专属体验，H&amp;amp;M致力于在Google+上提供不同于其他社交平台的内容，保证用户粘性和忠诚度，比如新品抢先预览、舞台幕后报道等。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;其次，H&amp;amp;M对Google+呈现的数据加以充分利用。&lt;/span&gt;内容的高分享度和圈子特性为H&amp;amp;M的营销团队提供了第一手数据，他们可以观察到谁进行了分享，再传播的路径是怎样的，谁的观点产生了重要影响。进而可以得知各圈子的意见领袖和各社区的结构特征，为日后的营销策略制定打下基础。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;社交媒体的运用是H&amp;amp;M整体营销策略布局当中的一部分，却是很重要的构成要件。无论是去年电商平台上线的“50 States of Fashion”活动，还是最近的超级碗campaign，都能看到小身影所迸发出的大能量。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;持续的话题讨论使活动保持热度。&lt;/span&gt;一年一度的超级碗向来是各大品牌的兵家必争之地，这次H&amp;amp;M同样采取了名人策略，邀请代言人贝克汉姆出演广告片。不同的是H&amp;amp;M为2月2日超级碗比赛当天的广告片打造了两个版本，播出哪一支的决定权掌握在消费者手中。活动一出，便在社交媒体上引起了热烈讨论，粉丝们纷纷参与投票，选出最期望看到的贝克汉姆的出场方式。在超级碗的舞台上，H&amp;amp;M算是入门选手，得益于社交媒体的实时跟进和推动，活动在整个赛事中一直保持热度，产生了很好的效果。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;意见领袖和激励机制的双重感召。&lt;/span&gt;2013年，为了推广在美国新上线的电商网站，H&amp;amp;M开展了名为“50 States of Fashion”的营销活动。H&amp;amp;M鼓励美国各州的消费者上传自己的照片到Instagram主页中，并以#HM Shop Online AL(Alabama)命名，H&amp;amp;M根据投票结果从参与者中选出每个州的“形象代言人”，获选者将获得1000美元的代金券以及去纽约参加时装周的机会。在整个活动中，H&amp;amp;M不仅号召普通消费者积极参与到活动中，同时邀请当地的意见领袖推动活动的传播。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;趣味活动激发用户的主动分享。&lt;/span&gt;2012年，H&amp;amp;M了提高纽约、洛杉矶和旧金山门店的客流量，决定通过LBS和移动互联网将潜在的线上消费者吸引到线下门店当中。吸引顾客走到线下的动力是什么?那就是设置在不同区域的贝克汉姆人像，用户根据H&amp;amp;M提供的线索找到分散在城市各处的人像。贝克汉姆的粉丝们可不会放过这个与偶像“亲密接触”的机会，顷刻间，与贝克汉姆人像合影和照片传遍社交网络，品牌曝光率瞬间提升。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;用户中心，以优质内容创造良好体验&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;社会化媒体让普通消费者能够集聚在品牌周围，形成有着共同兴趣爱好的社群，其活跃度和紧密度有赖于社群中心即品牌的表现。在社交平台的常规运营中，品牌与消费者的纽带就是内容，H&amp;amp;M正是凭借诚意十足的内容俘获了消费者的心。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;明确需求，投其所好，雨露均沾。&lt;/span&gt;社会化媒体固然是品牌进行自我呈现的有利平台，但一味地自说自话必定会失人心。H&amp;amp;M的定位是平民化时尚品牌，同时也深知自己的消费者是一群对时尚极度敏感的年轻人，他们关注各领域各层级的时尚潮流。因此，H&amp;amp;M在各个社交平台上并不局限于与品牌定位息息相关的内容，更会延伸至时尚的各个领域。尽管H&amp;amp;M在世界各地售卖的产品基本一致，不过在社交平台上却十分细心地照顾到不同地域消费者的喜好，创造出各种有趣话题，比如自行车时尚、布鲁克林的时尚未来派等等。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;爆点独家好料满足用户的好奇心。&lt;/span&gt;时装秀的后台是什么样的，广告片有什么有趣的幕后花絮，这些都是粉丝们好奇的内容，这也是H&amp;amp;M时常为粉丝们送上的重磅福利。这些幕后内容的放送可谓是一举多得，一方面获取容易，只需稍加留心进行记录即可，另一方面在满足用户好奇心的同时，更是品牌活动的再度传播，让用户更加全面深入地了解品牌。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;span style=&quot;font-weight:700;&quot;&gt;反观国内的情况：&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;以上均是H&amp;amp;M在国外社交媒体上的表现，无论是在日常运营还是在品牌活动中，H&amp;amp;M都积极使用各类社交平台与消费者对话，展现品牌形象。反观国内，H&amp;amp;M在中国市场中对社交媒体的运用远不如欧美国家。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;首先是平台的使用情况，H&amp;amp;M除了在新浪微博上及时更新内容之外，其在微信、人人等平台上的官方账号基本处于“半瘫痪”状态。那么内容上在此只能对H&amp;amp;M的微博进行观察，更新频率、内容形式基本与国外社交媒体账号保持同一步调。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;本土化内容主要体现在与国内明星和时尚界知名人士的互动，以及对国内店铺促销、品牌活动的宣传。相比而言，H&amp;amp;M对中国社交媒体的运用仍停留在品牌形象窗口的层面，粉丝互动程度和用户自发创造的内容都不如其在国外社交媒体的表现。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;不只是H&amp;amp;M，很多国际品牌在国外社交媒体上风生水起，却在国内表现平平，是策略重点不同还是水土不服，其中原因值得深思。&lt;/span&gt;&lt;/span&gt;&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;color:#444444;font-family:&#039;microsoft yahei&#039;;font-size:14px;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span&gt;&lt;span&gt;&lt;span&gt;\r\n	&lt;p style=&quot;color:#3E3E3E;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;color:#7F7F7F;&quot;&gt;&lt;span style=&quot;color:#FF0000;&quot;&gt;&lt;span style=&quot;background-color:#000000;&quot;&gt;&lt;span style=&quot;background-color:#FFFFFF;&quot;&gt;&lt;span style=&quot;color:#64451D;&quot;&gt;如果您喜欢我们的内容请动动小手指转发到您的朋友圈&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;color:#444444;font-family:&#039;Helvetica Neue&#039;, Helvetica, &#039;Hiragino Sans GB&#039;, &#039;Microsoft YaHei&#039;, 微软雅黑, Arial, sans-serif;font-size:16px;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;span&gt;&lt;b&gt;您还可以点击：&lt;a href=&quot;http://mp.weixin.qq.com/s?__biz=MzA3MTE4OTgyNg==&amp;amp;mid=201017818&amp;amp;idx=1&amp;amp;sn=d4fcf7d2a518d4b3fc1d4c4f971ffc21#rd&quot;&gt;亚篮网公众服务&lt;/a&gt;&amp;nbsp;&amp;nbsp;关注我们&lt;/b&gt;&lt;/span&gt;\r\n	&lt;/p&gt;\r\n&lt;br /&gt;\r\n&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;\r\n&lt;/p&gt;','','1415422855','1415435020','1','99630ff411650cfa','营销案例：H&amp;M社会化媒体营销案例分享','','184','0','0','0',NULL,'1','1','0','1','','');");

